// src/components/Chat.js
import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';
import './Chat.css';

const Chat = () => {
    const location = useLocation();
    const { username, token } = location.state || {};
    const [messages, setMessages] = useState([]);
    const [input, setInput] = useState('');
    const [ws, setWs] = useState(null);
    const [users, setUsers] = useState(['user1', 'user2', 'user3']); // Example user list

    useEffect(() => {
        if (username && token) {
            const websocket = new WebSocket(`ws://127.0.0.1:8000/ws/${username}?token=${token}`);
            websocket.onmessage = (event) => {
                setMessages((prevMessages) => [...prevMessages, JSON.parse(event.data)]);
            };
            websocket.onclose = () => {
                console.log('WebSocket connection closed');
            };
            websocket.onerror = (error) => {
                console.error('WebSocket error:', error);
            };
            setWs(websocket);
            return () => websocket.close();
        }
    }, [username, token]);

    const sendMessage = () => {
        if (ws && input.trim() !== '') {
            const message = { sender: username, content: input };
            ws.send(JSON.stringify(message));
            setInput('');
        }
    };

    return (
        <div className="chat-container">
            <div className="user-list">
                <h2>Users</h2>
                <ul>
                    {users.map((user, index) => (
                        <li key={index}>{user}</li>
                    ))}
                </ul>
            </div>
            <div className="chat-window">
                <div className="chat-header">
                    <h2>Chat</h2>
                </div>
                <div className="chat-body">
                    {messages.map((msg, index) => (
                        <div key={index} className={`message ${msg.sender === username ? 'sent' : 'received'}`}>
                            <span className="author">{msg.sender}</span>: <span className="content">{msg.content}</span>
                        </div>
                    ))}
                </div>
                <div className="message-input">
                    <input
                        type="text"
                        value={input}
                        onChange={(e) => setInput(e.target.value)}
                        onKeyPress={(e) => {
                            if (e.key === 'Enter') sendMessage();
                        }}
                    />
                    <button onClick={sendMessage}>Send</button>
                </div>
            </div>
        </div>
    );
};

export default Chat;
