import React, { useEffect, useState, useRef } from 'react';
import { useLocation } from 'react-router-dom';
import axios from 'axios'; // Add this line
import './Chat.css';

const Chat = () => {
    const location = useLocation();
    const { username, token } = location.state || {};
    const [messages, setMessages] = useState([]);
    const [input, setInput] = useState('');
    const [chatType, setChatType] = useState('');
    const [selectedUser, setSelectedUser] = useState('');
    const [selectedRoom, setSelectedRoom] = useState('');
    const [userStatuses, setUserStatuses] = useState({});
    const [rooms, setRooms] = useState([]);
    const websocketRef = useRef(null);

    useEffect(() => {
        // Fetch user statuses from the backend
        const fetchUserStatuses = async () => {
            try {
                const response = await fetch('http://127.0.0.1:8000/user_status');
                const data = await response.json();
                setUserStatuses(data);
            } catch (error) {
                console.error('Error fetching user statuses:', error);
            }
        };

        // Fetch available rooms from the backend
        const fetchRooms = async () => {
            try {
                const response = await fetch('http://127.0.0.1:8000/rooms');
                const data = await response.json();
                setRooms(data);
            } catch (error) {
                console.error('Error fetching rooms:', error);
            }
        };

        fetchUserStatuses();
        fetchRooms();
    }, []);

    useEffect(() => {
        if (username && token) {
            const ws = new WebSocket(`ws://127.0.0.1:8000/ws/${username}`);
            websocketRef.current = ws;

            ws.onopen = () => {
                console.log('WebSocket connection opened');
            };

            ws.onmessage = (event) => {
                const message = JSON.parse(event.data);
                if (message.type === 'message') {
                    setMessages((prevMessages) => [...prevMessages, message.data]);
                } else if (message.type === 'status') {
                    setUserStatuses(message.data);
                }
            };

            ws.onclose = () => {
                console.log('WebSocket connection closed');
            };

            ws.onerror = (error) => {
                console.error('WebSocket error:', error);
            };

            return () => {
                ws.close();
            };
        }
    }, [username, token]);

    const sendMessage = () => {
        if (input.trim() !== '') {
            if (chatType === 'one-to-one' && selectedUser) {
                websocketRef.current.send(JSON.stringify({ type: 'one-to-one', user: selectedUser, message: input }));
            } else if (chatType === 'room' && selectedRoom) {
                websocketRef.current.send(JSON.stringify({ type: 'room', room: selectedRoom, message: input }));
            } else if (chatType === 'group') {
                websocketRef.current.send(JSON.stringify({ type: 'group', message: input }));
            }
            setInput('');
        }
    };

    const handleUserSelect = (user) => {
        setChatType('one-to-one');
        setSelectedUser(user);
        setSelectedRoom('');
    };

    const handleRoomSelect = (room) => {
        setChatType('room');
        setSelectedRoom(room);
        setSelectedUser('');
    };

    const handleGroupChat = () => {
        setChatType('group');
        setSelectedUser('');
        setSelectedRoom('opcode convo');
    };

    const createRoom = async (roomName) => {
        try {
            await fetch('http://127.0.0.1:8000/create_room', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ room: roomName }),
            });
            setRooms((prevRooms) => [...prevRooms, roomName]);
        } catch (error) {
            console.error('Error creating room:', error);
        }
    };

    const createGroup = async () => {
        try {
            const groupName = "opcode convo";
            const members = ["user1", "user2", "user3", "user4", "user5"]; // Replace with actual usernames
            const response = await axios.post('http://127.0.0.1:8000/create_group', {
                group_name: groupName,
                usernames: members
            });
            console.log(response.data.message);
        } catch (error) {
            console.error('Error creating group:', error);
        }
    };

    const joinRoom = async (roomName) => {
        try {
            await fetch('http://127.0.0.1:8000/join_room', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ room: roomName, username }),
            });
            handleRoomSelect(roomName);
        } catch (error) {
            console.error('Error joining room:', error);
        }
    };

    return (
        <div className="chat-container">
            <div className="users-list">
                <h3>Users</h3>
                {Object.keys(userStatuses).map((user) => (
                    <div
                        key={user}
                        className={`user-item ${userStatuses[user] === 'online' ? 'online' : 'offline'}`}
                        onClick={() => handleUserSelect(user)}
                    >
                        {user} - {userStatuses[user]}
                    </div>
                ))}
                <div
                    className="user-item"
                    onClick={handleGroupChat}
                >
                    opcode convo - Group
                </div>
                <h3>Rooms</h3>
                {rooms.map((room) => (
                    <div
                        key={room}
                        className="room-item"
                        onClick={() => joinRoom(room)}
                    >
                        {room}
                    </div>
                ))}
                <input
                    type="text"
                    placeholder="New room name"
                    onKeyDown={(e) => {
                        if (e.key === 'Enter' && e.target.value.trim() !== '') {
                            createRoom(e.target.value.trim());
                            e.target.value = '';
                        }
                    }}
                />
            </div>
            <div className="chat-box">
                <h3>{chatType === 'one-to-one' ? `Chat with ${selectedUser}` : chatType === 'room' ? `Room: ${selectedRoom}` : 'Group Chat: opcode convo'}</h3>
                <div className="messages-list">
                    {messages.map((message, index) => (
                        <div key={index} className="message-item">
                            {message}
                        </div>
                    ))}
                </div>
                <div className="input-box">
                    <input
                        type="text"
                        value={input}
                        onChange={(e) => setInput(e.target.value)}
                        placeholder="Type a message"
                    />
                    <button onClick={sendMessage}>Send</button>
                </div>
            </div>
        </div>
    );
};

export default Chat;
