import React, { useEffect, useState, useRef } from 'react';
import { useLocation } from 'react-router-dom';
import './Chat.css';

const Chat = () => {
    const location = useLocation();
    const { username, token } = location.state || {};
    const [messages, setMessages] = useState([]);
    const [input, setInput] = useState('');
    const [chatType, setChatType] = useState('');
    const [selectedUser, setSelectedUser] = useState('');
    const [userStatuses, setUserStatuses] = useState({}); // Initialize as an empty object
    const websocketRef = useRef(null);

    useEffect(() => {
        const fetchUserStatuses = async () => {
            try {
                const response = await fetch('http://127.0.0.1:8000/user_status');
                const data = await response.json();
                // Ensure data is an object
                if (data && typeof data === 'object') {
                    setUserStatuses(data);
                } else {
                    console.error('Invalid user status data:', data);
                }
            } catch (error) {
                console.error('Error fetching user statuses:', error);
            }
        };

        fetchUserStatuses();
    }, []);

    useEffect(() => {
        if (username && token) {
            const ws = new WebSocket(`ws://127.0.0.1:8000/ws/${username}`);
            websocketRef.current = ws;

            ws.onopen = () => {
                console.log('WebSocket connection opened');
            };

            ws.onmessage = (event) => {
                const message = JSON.parse(event.data);
                if (message.type === 'message') {
                    // Add the received message to the messages state
                    setMessages((prevMessages) => [...prevMessages, `${message.user}: ${message.data}`]);
                } else if (message.type === 'status') {
                    setUserStatuses(message.data);
                } else if (message.type === 'error') {
                    console.error(message.data); // Handle errors
                }
            };

            ws.onclose = () => {
                console.log('WebSocket connection closed');
            };

            ws.onerror = (error) => {
                console.error('WebSocket error:', error);
            };

            return () => {
                ws.close();
            };
        }
    }, [username, token]);

    const sendMessage = () => {
        if (input.trim() !== '') {
            if (chatType === 'one-to-one' && selectedUser) {
                // Send direct message to selected user
                websocketRef.current.send(JSON.stringify({ 
                    type: 'one-to-one', 
                    recipient: selectedUser, 
                    message: input.trim() 
                }));
            } else if (chatType === 'group') {
                // Send message to the group
                websocketRef.current.send(JSON.stringify({ 
                    type: 'group', 
                    room: 'opcode convo', 
                    message: input.trim() 
                }));
            }
            setInput(''); // Clear input after sending
        }
    };

    const handleUserSelect = (user) => {
        setChatType('one-to-one');
        setSelectedUser(user);
        setMessages([]); // Clear messages when switching to one-to-one chat
    };

    const handleGroupChat = () => {
        setChatType('group');
        setSelectedUser('opcode convo'); // Assign group name for clarity
        setMessages([]); // Clear messages when switching to group chat

        // Fetch all messages in the group chat (optional)
        fetchGroupMessages();
    };

    const fetchGroupMessages = async () => {
        try {
            const response = await fetch('http://127.0.0.1:8000/group_messages/opcode_convo'); // Change to your actual endpoint
            const data = await response.json();
            if (data && data.messages) {
                setMessages(data.messages); // Assuming data contains an array of messages
            } else {
                console.error('Invalid group messages data:', data);
            }
        } catch (error) {
            console.error('Error fetching group messages:', error);
        }
    };

    return (
        <div className="chat-container">
            <div className="sidebar">
                <h3>Users</h3>
                <ul className="users-list">
                    {Object.keys(userStatuses).length > 0 ? (
                        Object.keys(userStatuses).map((user) => (
                            <li
                                key={user}
                                className={`user-item ${userStatuses[user] === 'online' ? 'online' : 'offline'}`}
                                onClick={() => handleUserSelect(user)}
                            >
                                {user} - {userStatuses[user]}
                            </li>
                        ))
                    ) : (
                        <li>No users available</li> // Fallback message when no users are available
                    )}
                    <li
                        className="user-item"
                        onClick={handleGroupChat}
                    >
                        opcode convo - Group
                    </li>
                </ul>
            </div>
            <div className="chat-content">
                <div className="chat-header">
                    <h3>Logged in as: {username}</h3>
                    <h3>{chatType === 'one-to-one' ? `Chat with ${selectedUser}` : 'Group Chat: opcode convo'}</h3>
                </div>
                <div className="chat-body">
                    <div className="messages-list">
                        {messages.map((message, index) => (
                            <div key={index} className="message-item">
                                {message}
                            </div>
                        ))}
                    </div>
                </div>
                <div className="message-input">
                    <input
                        type="text"
                        value={input}
                        onChange={(e) => setInput(e.target.value)}
                        placeholder="Type a message"
                    />
                    <button onClick={sendMessage}>Send</button>
                </div>
            </div>
        </div>
    );
};

export default Chat;
