import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';
import './GroupChat.css';

const GroupChat = () => {
    const location = useLocation();
    const { username } = location.state || {};
    const [messages, setMessages] = useState([]);
    const [newMessage, setNewMessage] = useState('');
    const ws = React.useRef(null);

    useEffect(() => {
        ws.current = new WebSocket(`ws://localhost:8000/ws/${username}`);

        ws.current.onopen = () => {
            console.log('Connected to WebSocket');
        };

        ws.current.onmessage = (event) => {
            const data = JSON.parse(event.data);
            if (data.type === 'message') {
                setMessages((prevMessages) => [...prevMessages, data.data]);
            } else if (data.type === 'status') {
                console.log('User status:', data.data);
            }
        };

        ws.current.onclose = () => {
            console.log('Disconnected from WebSocket');
        };

        return () => {
            ws.current.close();
        };
    }, [username]);

    const handleSendMessage = () => {
        const messageData = {
            type: 'group',
            room: 'opcode_convo',
            data: { sender: username, text: newMessage }, // Send message as an object with sender and text
        };
        ws.current.send(JSON.stringify(messageData));
        setNewMessage('');
    };

    const userProfilePics = {
        leesa: '/path/to/leesa_profile_pic.png',
        mohendran: '/path/to/mohendran_profile_pic.png',
        deepan: '/path/to/deepan_profile_pic.png',
        sathish: '/path/to/sathish_profile_pic.png',
        opcodeconvo: '/path/to/opcode_convo_group_pic.png'
    };

    return (
        <div className="group-chat-container">
            <div className="group-header">
                <img src={userProfilePics['opcodeconvo']} alt="Group" className="group-profile-pic" />
                <h2 className="group-title">opcode convo</h2>
            </div>
            <div className="messages-container">
                {messages.map((message, index) => (
                    <div key={index} className="message">
                        <img
                            src={userProfilePics[message.sender]}
                            alt={message.sender}
                            className="profile-pic"
                        />
                        <div className="message-content">
                            <span className="sender">{message.sender}</span>
                            <span className="text">{message.text}</span>
                        </div>
                    </div>
                ))}
            </div>
            <div className="input-container">
                <input
                    type="text"
                    value={newMessage}
                    onChange={(e) => setNewMessage(e.target.value)}
                    placeholder="Type a message"
                    className="input-field"
                />
                <button onClick={handleSendMessage} className="send-button">Send</button>
            </div>
        </div>
    );
};

export default GroupChat;
