import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './Login.css'; // Import the CSS file

const Login = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const navigate = useNavigate();

    const handleSubmit = async (event) => {
        event.preventDefault();
        setError(''); // Clear previous error messages

        if (username.trim() === '' || password.trim() === '') {
            setError('Username and password are required.');
            return;
        }

        try {
            const response = await axios.post('http://127.0.0.1:8000/login', {
                username,
                password
            });
            console.log('Login successful', response.data);
            navigate('/chat'); // Navigate to chat page on successful login
        } catch (err) {
            if (err.response && err.response.data) {
                const errorData = err.response.data;
                
                // Check if error detail is an array and extract messages
                if (Array.isArray(errorData.detail)) {
                    const errorMessages = errorData.detail.map(e => e.msg || e.message).join(', ');
                    setError(errorMessages);
                } else {
                    // If detail is not an array, use it directly
                    setError(errorData.detail || 'An error occurred.');
                }
            } else {
                // Handle unexpected errors
                setError('An unexpected error occurred. Please try again later.');
            }
        }
    };

    return (
        <div className="login-container">
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <label>Username</label>
                    <input
                        type="text"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                        required
                    />
                </div>
                <div className="form-group">
                    <label>Password</label>
                    <input
                        type="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        required
                    />
                </div>
                <button type="submit" className="submit-button">Login</button>
            </form>
            {error && <p className="error-message">{error}</p>}
        </div>
    );
};

export default Login;
