// src/components/Login.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './Login.css';

const Login = () => {
    const [username, setUsername] = useState('user2');  // Default username
    const [password, setPassword] = useState('password2');  // Default password
    const [error, setError] = useState('');
    const navigate = useNavigate();

    useEffect(() => {
        // Automatically fill in the default credentials when the component mounts
        setUsername('user2');
        setPassword('password2');
    }, []);

    const handleSubmit = async (event) => {
        event.preventDefault();
        try {
            const response = await axios.post('http://127.0.0.1:8000/token', new URLSearchParams({
                username,
                password
            }));
            const { access_token } = response.data;
            navigate('/chat', { state: { username, token: access_token } });
        } catch (error) {
            setError('Login failed. Please check your username and password.');
            console.error('Login error', error);
        }
    };

    return (
        <div className="login-container">
            <div className="login-box">
                <h2>Login</h2>
                <form onSubmit={handleSubmit}>
                    <div>
                        <label>Username:</label>
                        <input
                            type="text"
                            value={username}
                            onChange={(e) => setUsername(e.target.value)}
                            required
                        />
                    </div>
                    <div>
                        <label>Password:</label>
                        <input
                            type="password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            required
                        />
                    </div>
                    <button type="submit">Login</button>
                </form>
                {error && <p>{error}</p>}
            </div>
        </div>
    );
};

export default Login;
