import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';
import './GroupChat.css'; // Import the new CSS file

const GroupChat = () => {
    const location = useLocation();
    const { username } = location.state || {};
    const [messages, setMessages] = useState([]);

    useEffect(() => {
        fetchGroupMessages();
    }, []);

    const fetchGroupMessages = async () => {
        try {
            const response = await fetch('http://127.0.0.1:8000/group_messages/opcode_convo');
            const data = await response.json();
            if (data && data.messages) {
                setMessages(data.messages);
            }
        } catch (error) {
            console.error('Error fetching group messages:', error);
        }
    };

    return (
        <div className="chat-container">
            <div className="sidebar">
                <h3>Users</h3>
                <ul className="users-list">
                    {/* Replace this with dynamic user list */}
                    <li className="user-item">Leesa</li>
                    <li className="user-item">Mohendran</li>
                    <li className="user-item">Deepan</li>
                    <li className="user-item">Sathish</li>
                </ul>
            </div>
            <div className="chat-content">
                <div className="chat-header">
                    <h1>Opcode Convo</h1> {/* Centered group chat name */}
                </div>
                <div className="chat-body">
                    <div className="messages-list">
                        {messages.map((message, index) => (
                            <div key={index} className="message-item">
                                {message}
                            </div>
                        ))}
                    </div>
                </div>
                <div className="message-input">
                    <input type="text" placeholder="Type a message" />
                    <button>Send</button>
                </div>
            </div>
        </div>
    );
};

export default GroupChat;
