import React, { useEffect, useState, useRef } from 'react';
import { useLocation } from 'react-router-dom';
import './Chat.css';

const Chat = () => {
    const location = useLocation();
    const { username, token } = location.state || {};
    const [messages, setMessages] = useState([]);
    const [input, setInput] = useState('');
    const [chatType, setChatType] = useState('group');
    const [selectedUser, setSelectedUser] = useState('');
    const websocketRef = useRef(null);

    useEffect(() => {
        if (username && token) {
            const websocket = new WebSocket(`ws://127.0.0.1:8000/ws/${username}?token=${token}`);
            websocketRef.current = websocket;

            websocket.onopen = () => {
                console.log('WebSocket connection opened');
            };

            websocket.onmessage = (event) => {
                setMessages((prevMessages) => [...prevMessages, event.data]);
            };

            websocket.onclose = () => {
                console.log('WebSocket connection closed');
            };

            websocket.onerror = (error) => {
                console.error('WebSocket error:', error);
            };

            return () => {
                websocket.close();
            };
        }
    }, [username, token]);

    const sendMessage = () => {
        if (input.trim() !== '') {
            const ws = websocketRef.current;
            if (ws && ws.readyState === WebSocket.OPEN) {
                let message;
                if (chatType === 'one-to-one' && selectedUser) {
                    message = `${selectedUser}:${input}`;  // Format for one-to-one chat
                } else {
                    message = `group:${input}`;  // Group chat prefix
                }
                ws.send(message);
                setInput('');
            } else {
                console.error('WebSocket connection is not open');
            }
        }
    };

    const handleChatTypeChange = (type) => {
        setChatType(type);
        setSelectedUser('');  // Clear selected user for group chat
        setMessages([]);  // Optionally clear messages when changing chat type
    };

    const handleUserClick = (user) => {
        if (chatType === 'one-to-one') {
            setSelectedUser(user);
            setMessages([]);  // Clear messages when changing to a new user
        }
    };

    return (
        <div className="chat-container">
            <div className="sidebar">
                <h3>Users</h3>
                <div className="chat-type-buttons">
                    <button
                        onClick={() => handleChatTypeChange('one-to-one')}
                        className={chatType === 'one-to-one' ? 'active' : ''}
                    >
                        One-to-One
                    </button>
                    <button
                        onClick={() => handleChatTypeChange('group')}
                        className={chatType === 'group' ? 'active' : ''}
                    >
                        Group Chat
                    </button>
                </div>
                <ul>
                    {['user1', 'user2', 'user3'].map((user, index) => (
                        <li
                            key={index}
                            onClick={() => handleUserClick(user)}
                            className={selectedUser === user ? 'selected' : ''}
                        >
                            {user}
                        </li>
                    ))}
                </ul>
            </div>
            <div className="chat-content">
                <div className="chat-header">
                    <h2>
                        {chatType === 'group'
                            ? `Group Chat - ${username}`
                            : `One-to-One Chat - ${selectedUser}`}
                    </h2>
                </div>
                <div className="chat-body">
                    {messages.map((msg, index) => (
                        <div
                            key={index}
                            className={`message ${msg.startsWith(username) ? 'sent' : 'received'}`}
                        >
                            <div className="content">{msg}</div>
                        </div>
                    ))}
                </div>
                <div className="message-input">
                    <input
                        type="text"
                        value={input}
                        onChange={(e) => setInput(e.target.value)}
                        onKeyPress={(e) => {
                            if (e.key === 'Enter') sendMessage();
                        }}
                    />
                    <button onClick={sendMessage}>Send</button>
                </div>
            </div>
        </div>
    );
};

export default Chat;
