// src/components/Chat.js
import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';
import './Chat.css';

const Chat = () => {
    const location = useLocation();
    const { username, token } = location.state || {};
    const [messages, setMessages] = useState([]);
    const [input, setInput] = useState('');
    const [selectedUser, setSelectedUser] = useState(null);
    const [ws, setWs] = useState(null);

    useEffect(() => {
        if (username && token) {
            const websocket = new WebSocket(`ws://127.0.0.1:8000/ws/${username}?token=${token}`);
            websocket.onmessage = (event) => {
                setMessages((prevMessages) => [...prevMessages, event.data]);
            };
            websocket.onclose = () => {
                console.log('WebSocket connection closed');
            };
            websocket.onerror = (error) => {
                console.error('WebSocket error:', error);
            };
            setWs(websocket);
            return () => websocket.close();
        }
    }, [username, token]);

    const sendMessage = () => {
        if (ws && input.trim() !== '' && selectedUser) {
            ws.send(`${selectedUser}:${input}`);
            setInput('');
        }
    };

    const handleUserClick = (user) => {
        setSelectedUser(user);
        setMessages([]); // Clear messages for new chat
    };

    return (
        <div className="chat-container">
            <div className="sidebar">
                <h3>Users</h3>
                <button onClick={() => handleUserClick(null)}>Group Chat</button>
                <button onClick={() => handleUserClick("user1")}>One-to-One Chat with user1</button>
                <button onClick={() => handleUserClick("user2")}>One-to-One Chat with user2</button>
                <button onClick={() => handleUserClick("user3")}>One-to-One Chat with user3</button>
            </div>
            <div className="chat-content">
                <div className="chat-header">
                    <h2>{selectedUser ? `Chat with ${selectedUser}` : 'Group Chat'}</h2>
                </div>
                <div className="chat-body">
                    {messages.map((msg, index) => (
                        <div key={index} className={`message ${msg.startsWith(username) ? 'sent' : 'received'}`}>
                            <div className="content">{msg}</div>
                        </div>
                    ))}
                </div>
                <div className="message-input">
                    <input
                        type="text"
                        value={input}
                        onChange={(e) => setInput(e.target.value)}
                        onKeyPress={(e) => {
                            if (e.key === 'Enter') sendMessage();
                        }}
                    />
                    <button onClick={sendMessage}>Send</button>
                </div>
            </div>
        </div>
    );
};

export default Chat;
