import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './Signup.css';

const Signup = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const [success, setSuccess] = useState('');  // New state for success message
    const navigate = useNavigate();

    const handleSubmit = async (event) => {
        event.preventDefault();
        try {
            const response = await axios.post('http://127.0.0.1:8000/signup', new URLSearchParams({
                username,
                password
            }), {
                headers: {
                    'Content-Type': 'application/x-www-form-urlencoded'
                }
            });

            if (response.status === 200 && response.data.message === 'User created successfully') {
                setSuccess('Signup successful!');  // Set success message
                setTimeout(() => {
                    // Assuming you might get a token in the response, adjust accordingly
                    const token = response.data.token; // Adjust based on your backend response
                    navigate('/chat', { state: { username, token } }); // Redirect to chat page with username and token
                }, 1500);
            } else {
                setError('Sign up failed. Please try again.');
            }
        } catch (error) {
            setError('Sign up failed. Please try again.');
            console.error('Sign up error', error.response ? error.response.data : error.message);
        }
    };

    return (
        <div className="signup-container">
            <div className="signup-box">
                <h2>Sign Up</h2>
                <form onSubmit={handleSubmit}>
                    <div>
                        <label>Username:</label>
                        <input
                            type="text"
                            value={username}
                            onChange={(e) => setUsername(e.target.value)}
                            required
                        />
                    </div>
                    <div>
                        <label>Password:</label>
                        <input
                            type="password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            required
                        />
                    </div>
                    <button type="submit">Sign Up</button>
                </form>
                {error && <p className="error-message">{error}</p>}
                {success && <p className="success-message">{success}</p>}  {/* Display success message */}
            </div>
        </div>
    );
};

export default Signup;
