// src/components/Signup.js
import React, { useState } from 'react';
import axios from 'axios';
import { useHistory } from 'react-router-dom'; // Import useHistory for navigation
import './UserForm.css'; // Ensure this file exists and is correctly referenced

const Signup = () => {
    const [formState, setFormState] = useState({ username: '', password: '' });
    const [message, setMessage] = useState('');
    const history = useHistory(); // Initialize useHistory

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormState((prevState) => ({ ...prevState, [name]: value }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('http://127.0.0.1:8000/users', formState);
            setMessage(response.data.message);
            history.push('/chat', { username: formState.username }); // Redirect to chat page with username
        } catch (error) {
            setMessage('Error signing up: ' + (error.response?.data?.detail || error.message));
        }
    };

    return (
        <div className="form-container">
            <form onSubmit={handleSubmit}>
                <h2>Sign Up</h2>
                <input
                    type="text"
                    name="username"
                    placeholder="Username"
                    value={formState.username}
                    onChange={handleChange}
                    required
                />
                <input
                    type="password"
                    name="password"
                    placeholder="Password"
                    value={formState.password}
                    onChange={handleChange}
                    required
                />
                <button type="submit">Sign Up</button>
                {message && <p>{message}</p>}
            </form>
        </div>
    );
};

export default Signup;
