import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';
import './GroupChat.css';

const userProfilePics = {
    leesa: require('./images/Frame 11.png'),
    mohendran: require('./images/Frame 30.png'),
    deepan: require('./images/Frame 12 (1).png'),
    sathish: require('./images/Frame 12.png'),
    opcodeconvo: require('./images/Frame 1.png'),
};

const GroupChat = () => {
    const location = useLocation();
    const { username } = location.state || {};
    const [messages, setMessages] = useState([]);
    const [newMessage, setNewMessage] = useState('');

    useEffect(() => {
        const ws = new WebSocket(`ws://localhost:8000/ws/${username}`);

        ws.onopen = () => {
            console.log('Connected to WebSocket');
        };

        ws.onmessage = (event) => {
            const data = JSON.parse(event.data);
            if (data.type === 'message') {
                setMessages((prevMessages) => [...prevMessages, data.data]);
            } else if (data.type === 'status') {
                console.log('User status:', data.data);
            }
        };

        ws.onclose = () => {
            console.log('Disconnected from WebSocket');
        };

        return () => {
            ws.close();
        };
    }, [username]);

    const handleSendMessage = () => {
        const ws = new WebSocket(`ws://localhost:8000/ws/${username}`);
        const messageData = {
            type: 'group',
            room: 'opcode_convo',
            data: newMessage,
        };
        ws.onopen = () => {
            ws.send(JSON.stringify(messageData));
            setNewMessage('');
        };
    };

    return (
        <div className="group-chat-container">
            <div className="group-header">
                <img src={userProfilePics['opcodeconvo']} alt="Group" className="group-profile-pic" />
                <h2 className="group-title">opcode convo</h2>
            </div>
            <div className="messages-container">
                {messages.map((message, index) => (
                    <div key={index} className="message">
                        <img
                            src={userProfilePics[message.sender]}
                            alt={message.sender}
                            className="profile-pic"
                        />
                        <div className="message-content">
                            <span className="sender">{message.sender}</span>
                            <span className="text">{message.text}</span>
                        </div>
                    </div>
                ))}
            </div>
            <div className="input-container">
                <input
                    type="text"
                    value={newMessage}
                    onChange={(e) => setNewMessage(e.target.value)}
                    placeholder="Type a message"
                    className="input-field"
                />
                <button onClick={handleSendMessage} className="send-button">Send</button>
            </div>
        </div>
    );
};

export default GroupChat;
