// src/components/Signup.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './UserForm.css';

const Signup = () => {
    const [formState, setFormState] = useState({ username: '', password: '' });
    const [message, setMessage] = useState('');
    const [users, setUsers] = useState([]);

    useEffect(() => {
        const fetchUsers = async () => {
            try {
                const response = await axios.get('http://127.0.0.1:8000/user_status');
                console.log('Fetched users:', response.data); // Debugging line
                if (Array.isArray(response.data)) {
                    setUsers(response.data);
                } else {
                    console.error("Unexpected data format:", response.data);
                    setUsers([]);
                }
            } catch (error) {
                console.error('Error fetching users:', error);
            }
        };

        fetchUsers();
    }, []);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormState((prevState) => ({ ...prevState, [name]: value }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('http://127.0.0.1:8000/signup', formState);
            setMessage(response.data.message);
            // Fetch users again after signup
            const usersResponse = await axios.get('http://127.0.0.1:8000/user_status');
            setUsers(usersResponse.data);
        } catch (error) {
            setMessage('Error signing up: ' + (error.response?.data?.detail || error.message));
        }
    };

    return (
        <div className="form-container">
            <form onSubmit={handleSubmit}>
                <h2>Sign Up</h2>
                <input
                    type="text"
                    name="username"
                    placeholder="Username"
                    value={formState.username}
                    onChange={handleChange}
                />
                <input
                    type="password"
                    name="password"
                    placeholder="Password"
                    value={formState.password}
                    onChange={handleChange}
                />
                <button type="submit">Sign Up</button>
                {message && <p>{message}</p>}
            </form>
            <div>
                <h2>Users</h2>
                {Array.isArray(users) ? (
                    <ul>
                        {users.map(user => (
                            <li key={user.username}>{user.username} ({user.status})</li>
                        ))}
                    </ul>
                ) : (
                    <p>No users available</p>
                )}
            </div>
        </div>
    );
};

export default Signup;
