import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';
import './GroupChat.css';

const userProfilePics = {
    leesa: require('./images/Frame 11.png'),
    mohendran: require('./images/Frame 30.png'),
    deepan: require('./images/Frame 12 (1).png'),
    sathish: require('./images/Frame 12.png'),
    opcodeconvo: require('./images/Frame 1.png'),
};

const GroupChat = () => {
    const location = useLocation();
    const { username } = location.state || {};
    const [messages, setMessages] = useState([]);
    const [newMessage, setNewMessage] = useState('');

    useEffect(() => {
        fetchGroupMessages();
    }, []);

    const fetchGroupMessages = async () => {
        try {
            const response = await fetch('http://127.0.0.1:8000/group_messages/opcode_convo');
            const data = await response.json();
            if (data && data.messages) {
                setMessages(data.messages);
            }
        } catch (error) {
            console.error('Error fetching group messages:', error);
        }
    };

    const handleSendMessage = async () => {
        if (newMessage.trim()) {
            const messageData = {
                sender: username,
                text: newMessage,
            };

            try {
                const response = await fetch('http://127.0.0.1:8000/send_message', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    body: JSON.stringify(messageData),
                });

                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }

                setMessages((prevMessages) => [...prevMessages, { sender: username, text: newMessage }]);
                setNewMessage(''); // Clear the input field
            } catch (error) {
                console.error('Error sending message:', error);
            }
        }
    };

    return (
        <div className="chat-container">
            <div className="sidebar">
                <h3>Users</h3>
                <ul className="users-list">
                    <li className="user-item">Leesa</li>
                    <li className="user-item">Mohendran</li>
                    <li className="user-item">Deepan</li>
                    <li className="user-item">Sathish</li>
                </ul>
            </div>
            <div className="chat-content">
                <div className="chat-header">
                    <h1>Opcode Convo</h1>
                </div>
                <div className="chat-body">
                    <div className="messages-list">
                        {messages.map((message, index) => (
                            <div key={index} className={`message-item ${message.sender === username ? 'sent' : 'received'}`}>
                                <div className="profile-pic">
                                    <img src={userProfilePics[message.sender]} alt={`${message.sender}'s profile`} />
                                </div>
                                <div className="message-text">
                                    <span>{message.sender}: </span>
                                    {message.text}
                                </div>
                            </div>
                        ))}
                    </div>
                </div>
                <div className="message-input">
                    <input
                        type="text"
                        placeholder="Type a message"
                        value={newMessage}
                        onChange={(e) => setNewMessage(e.target.value)}
                    />
                    <button onClick={handleSendMessage}>Send</button>
                </div>
                <div className="username-display">
                    <p>Logged in as: {username}</p>
                </div>
            </div>
        </div>
    );
};

export default GroupChat;
