import React, { useEffect, useState, useRef } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import './Chat.css';

const Chat = () => {
    const location = useLocation();
    const navigate = useNavigate();
    const { username: initialUsername, token } = location.state || {};
    const [messages, setMessages] = useState([]);
    const [input, setInput] = useState('');
    const [chatType, setChatType] = useState('group');
    const [selectedUser, setSelectedUser] = useState('');
    const [userStatuses, setUserStatuses] = useState({});
    const [users, setUsers] = useState([]);
    const websocketRef = useRef(null);

    useEffect(() => {
        if (!initialUsername || !token) {
            navigate('/login');
            return;
        }

        const fetchInitialData = async () => {
            try {
                const [statusResponse, usersResponse] = await Promise.all([
                    fetch('http://127.0.0.1:8000/user_status'),
                    fetch('http://127.0.0.1:8000/users')
                ]);
                const statusData = await statusResponse.json();
                const usersData = await usersResponse.json();
                setUserStatuses(statusData);
                setUsers(usersData);
            } catch (error) {
                console.error('Error fetching initial data:', error);
            }
        };

        fetchInitialData();
    }, [initialUsername, token, navigate]);

    useEffect(() => {
        if (initialUsername && token) {
            // Close existing WebSocket connection if it exists
            if (websocketRef.current) {
                websocketRef.current.close();
            }

            const websocket = new WebSocket(`ws://127.0.0.1:8000/ws/${initialUsername}?token=${token}`);
            websocketRef.current = websocket;

            websocket.onopen = () => {
                console.log('WebSocket connection opened');
            };

            websocket.onmessage = (event) => {
                try {
                    const messageData = JSON.parse(event.data);
                    if (messageData.type === 'status') {
                        setUserStatuses(messageData.data);
                    } else if (messageData.type === 'message') {
                        setMessages((prevMessages) => [...prevMessages, messageData.data]);
                    } else if (messageData.type === 'user_list') {
                        setUsers(messageData.data);
                    }
                } catch (error) {
                    console.error('Error parsing WebSocket message:', error);
                }
            };

            websocket.onclose = () => {
                console.log('WebSocket connection closed');
            };

            websocket.onerror = (error) => {
                console.error('WebSocket error:', error);
            };

            return () => {
                if (websocketRef.current) {
                    websocketRef.current.close();
                }
            };
        }
    }, [initialUsername, token]);

    const sendMessage = () => {
        if (input.trim() === '') return;

        const ws = websocketRef.current;
        if (ws && ws.readyState === WebSocket.OPEN) {
            try {
                const message = chatType === 'one-to-one' && selectedUser
                    ? JSON.stringify({ type: 'one-to-one', content: input, recipient: selectedUser })
                    : JSON.stringify({ type: 'group', content: input });

                ws.send(message);
                setInput('');
            } catch (error) {
                console.error('Error sending message:', error);
            }
        }
    };

    const handleLogout = () => {
        // Clear the token and redirect to login page
        navigate('/login');
    };

    return (
        <div className="chat-container">
            <div className="sidebar">
                <h3>Users</h3>
                <div className="chat-type-buttons">
                    <button onClick={() => setChatType('group')}>Group Chat</button>
                    <button onClick={() => setChatType('one-to-one')}>One-to-One Chat</button>
                </div>
                <ul>
                    {users.map((user) => (
                        <li
                            key={user.username}
                            className={selectedUser === user.username ? 'selected' : ''}
                            onClick={() => setSelectedUser(user.username)}
                        >
                            <div className="user-info">
                                <span className="username">{user.username}</span>
                                <span className={`status ${userStatuses[user.username] === 'online' ? 'online' : 'offline'}`}></span>
                            </div>
                        </li>
                    ))}
                </ul>
                <button className="logout-button" onClick={handleLogout}>Logout</button>
            </div>
            <div className="chat-content">
                <div className="chat-header">
                    <h2>{initialUsername} - {chatType === 'one-to-one' ? selectedUser : 'Group Chat'}</h2>
                </div>
                <div className="chat-body">
                    {messages.map((msg, index) => (
                        <div key={index} className={`message ${msg.sender === initialUsername ? 'sent' : 'received'}`}>
                            <div className="content">{msg.content}</div>
                        </div>
                    ))}
                </div>
                <div className="message-input">
                    <input
                        type="text"
                        value={input}
                        onChange={(e) => setInput(e.target.value)}
                        placeholder="Type your message..."
                    />
                    <button onClick={sendMessage}>Send</button>
                </div>
            </div>
        </div>
    );
};

export default Chat;
