import React, { useEffect, useState, useRef } from 'react';
import { useLocation } from 'react-router-dom';
import './Chat.css';

const Chat = () => {
    const location = useLocation();
    const { username, token } = location.state || {};
    const [messages, setMessages] = useState([]);
    const [input, setInput] = useState('');
    const [chatType, setChatType] = useState('group');
    const [selectedUser, setSelectedUser] = useState('');
    const [userStatuses, setUserStatuses] = useState({});
    const websocketRef = useRef(null);

    // Fetch user statuses from the backend on component mount
    useEffect(() => {
        const fetchUserStatuses = async () => {
            try {
                const response = await fetch('http://127.0.0.1:8000/user_status');
                const data = await response.json();
                setUserStatuses(data);
            } catch (error) {
                console.error('Error fetching user statuses:', error);
            }
        };

        fetchUserStatuses();
    }, []);

    // Establish WebSocket connection and handle incoming messages
    useEffect(() => {
        const connectWebSocket = () => {
            if (username && token) {
                const websocket = new WebSocket(`ws://127.0.0.1:8000/ws/${username}?token=${token}`);
                websocketRef.current = websocket;

                websocket.onopen = () => {
                    console.log('WebSocket connection opened');
                };

                websocket.onmessage = (event) => {
                    try {
                        const messageData = JSON.parse(event.data);
                        if (messageData.type === 'status') {
                            setUserStatuses(messageData.data);
                        } else if (messageData.type === 'message') {
                            setMessages((prevMessages) => [...prevMessages, messageData.data]);
                        }
                    } catch (error) {
                        console.error('Error parsing WebSocket message:', error);
                    }
                };

                websocket.onclose = () => {
                    console.log('WebSocket connection closed, attempting to reconnect');
                    setTimeout(connectWebSocket, 1000);
                };

                websocket.onerror = (error) => {
                    console.error('WebSocket error:', error);
                };

                window.addEventListener('beforeunload', () => {
                    if (websocketRef.current) {
                        websocketRef.current.close();
                    }
                });

                return () => {
                    if (websocketRef.current) {
                        websocketRef.current.close();
                    }
                };
            }
        };

        connectWebSocket();
    }, [username, token]);

    // Handle sending messages
    const sendMessage = () => {
        if (input.trim() === '') return;

        const ws = websocketRef.current;
        if (ws && ws.readyState === WebSocket.OPEN) {
            try {
                const message = chatType === 'one-to-one' && selectedUser
                    ? `${selectedUser}:${input}`  // Format for one-to-one chat
                    : input;  // Group chat (send as is)

                // Send message to WebSocket server
                ws.send(message);

                // Append message to sender's message list
                setMessages((prevMessages) => [
                    ...prevMessages,
                    `You: ${input}`,
                ]);
                setInput('');
            } catch (error) {
                console.error('Error sending message:', error);
            }
        }
    };

    // Handle changing chat type
    const handleChatTypeChange = (event) => {
        setChatType(event.target.value);
    };

    return (
        <div className="chat-container">
            <div className="sidebar">
                <h3>Users</h3>
                <ul className="user-list">
                    {Object.keys(userStatuses).map((user) => (
                        <li
                            key={user}
                            className={userStatuses[user] === 'online' ? 'online' : 'offline'}
                            onClick={() => {
                                setChatType('one-to-one');
                                setSelectedUser(user);
                            }}
                        >
                            {user} - {userStatuses[user]}
                        </li>
                    ))}
                </ul>
            </div>
            <div className="chat-box">
                <h2>{chatType === 'one-to-one' ? `Chat with ${selectedUser}` : 'Group Chat'}</h2>
                <div className="messages">
                    {messages.map((message, index) => (
                        <div key={index} className="message">
                            {message}
                        </div>
                    ))}
                </div>
                <div className="input-box">
                    <input
                        type="text"
                        value={input}
                        onChange={(e) => setInput(e.target.value)}
                        placeholder="Type a message..."
                    />
                    <button onClick={sendMessage}>Send</button>
                </div>
            </div>
            <div className="chat-type-switcher">
                <label>
                    <input
                        type="radio"
                        value="group"
                        checked={chatType === 'group'}
                        onChange={handleChatTypeChange}
                    />
                    Group Chat
                </label>
                <label>
                    <input
                        type="radio"
                        value="one-to-one"
                        checked={chatType === 'one-to-one'}
                        onChange={handleChatTypeChange}
                    />
                    One-to-One Chat
                </label>
            </div>
        </div>
    );
};

export default Chat;
